import React from "react";
import Modal from "./Component/Modal";
import medalla from "../src/Img/medalla2.png"
import "./styles.css";
class App extends React.Component {
  state = {
    show: false
  };
  showModal = e => {
    this.setState({
      show: !this.state.show
    });
  };
  render() {
    return (
      <div className="App">
        <button
          class="toggle-button"
          id="centered-toggle-button"
          onClick={e => {
            this.showModal(e);
          }}
        >
          {" "}
          show Modal{" "}
        </button>

        <Modal onClose={this.showModal} show={this.state.show}>
        <div className="Modal">
                <div className="Nombre">
                    <h1>Richard Alvarez Ruiz</h1>
                </div>

                <div className="Cuerpo">
                    <h4>ha asisdito al evento de UPC </h4>
                    <img className="meda" src={medalla}></img>
                </div>

                <div className="medalla">
                {/* <img src={medalla}></img> */}
                </div>



          </div>
        </Modal>
      </div>
    );
  }
}

export default App;
